cipher_text = ""
second_text = ""

rots = 0
for i in range(len(cipher_text)):
    print(f"Key length of: {i}")
    coincidence = 0
    for j in range(len(cipher_text)):
        if cipher_text[j] == second_text[j]:
            coincidence += 1
    print(f"Coincidence = {100 * (coincidence/(len(cipher_text) - rots))}%\n")
    second_text = " " + second_text

